import { AppState } from './AppState';

describe('AppState', () => {
  let appState;

  beforeEach(() => {
    appState = new AppState();
  });

  it('starts with empty input image data', () => {
    expect(appState.inputImageData).toBe('');
  });

  it('sets input image data', () => {
    const mockData =
      'data:image/png;base64,';
    appState.setInputImageData(mockData);
    expect(appState.inputImageData).toBe(mockData);
  });
});
